mapper.select:
select
    *
from
    t_inventory_log
where
    commodity_name like CONCAT('%',#{inventoryLog.commodityName},'%')

    and order_number =#{inventoryLog.orderNumber}


    and operation_time>=#{startDateTime} and operation_time <= #{endDateTime}


    and operation_time>=#{startDateTime}


    and operation_time <= #{endDateTime}
